package com.focustech.validation.core;import java.util.List;/** * The configuration resolver . The resolver is used by the ValidateContext to resolve validators and configurations. * And then the validators will be registered in the ValidatorRegistry , and the configurations will be stored in the * ConfigInfo. *  * @see XMLConfigResolver * @author Paul */public interface ConfigResolver {    /**     * Resolve all Configurations,and then return the ConfigInfo contains the configurations.     */    public ConfigInfo resolveConfig() throws Exception;    /**     * Resolve all validators.     */    public List<AbstractValidator> resolveValidator() throws Exception;}